mapper.insert:
INSERT INTO email_verification (
    id, email, code, purpose, status, expire_at, created_at, used_at, client_ip, user_agent
)
VALUES (
    #{id}, #{email}, #{code}, #{purpose}, #{status}, #{expireAt}, #{createdAt}, #{usedAt}, #{clientIp}, #{userAgent}
)

mapper.update:
UPDATE email_verification
SET status = #{status}
    , used_at = #{usedAt}
WHERE id = #{id}

UPDATE email_verification
SET status = 1, used_at = #{usedAt}
WHERE id = #{id}

UPDATE email_verification
SET status = 2
WHERE id = #{id}
