mapper.insert:
INSERT INTO user_account (
    id, uuid, email, display_name, password_hash, password_salt, status,
    is_email_verified, created_at, updated_at, last_login_at, deleted
)
VALUES (
    #{id}, #{uuid}, #{email}, #{displayName}, #{passwordHash}, #{passwordSalt}, #{status},
    #{isEmailVerified}, #{createdAt}, #{updatedAt}, #{lastLoginAt}, #{deleted}
)

mapper.update:
UPDATE user_account
email = #{email}, display_name = #{displayName}, password_hash = #{passwordHash}, password_salt = #{passwordSalt}, status = #{status}, is_email_verified = #{isEmailVerified}, updated_at = #{updatedAt}, last_login_at = #{lastLoginAt}, deleted = #{deleted},
WHERE id = #{id}

UPDATE user_account
SET deleted = 1
WHERE id = #{id}

UPDATE user_account
SET last_login_at = #{lastLoginAt}, updated_at = #{lastLoginAt}
WHERE id = #{id}
